Container ABC(String image,String heading)
{
  return Container(
              width:160,
              decoration: BoxDecoration(
      border:Border.all(
        color:Color(0xff185c20),
        width:3,
      ),
      borderRadius: BorderRadius.circular(15)
    ),
              child:Card(
                child:Wrap(children: <Widget>[
                  Container(
                    height:180,
                    child:Image.network(image,fit: BoxFit.fill,)),
                  ListTile(
                    title:Text(heading,style:TextStyle(fontWeight: FontWeight.bold)),
                    subtitle: Column(
                      children: [

                         Row(
                           children: [
                             Icon(Icons.star,color: Colors.yellow,size: 15,),
                              SizedBox(width:5),
                     Text(' 5.0 (23 Reviews)')
                           ],
                         ),
                      ],
                    )
                  )
                ],)
              )
            );
}

Container categories(String heading,String subheading){
  return Container(
              width:160,
              decoration: BoxDecoration(
      border:Border.all(
        color:Color(0xff185c20),
        width:3,
      ),
      borderRadius: BorderRadius.circular(15)
    ),
              child:Card(
                child:Wrap(children: <Widget>[
                  ListTile(
                    title:Text(heading,style:TextStyle(fontWeight: FontWeight.bold)),
                    subtitle:Text(subheading) ,
                  )
                ],)
              )
            );
}


Container image(String text,String image)
{
  return Container(
              width:160,
              decoration: BoxDecoration(
      border:Border.all(
        color:Color(0xff185c20),
        width: 3,
      ),
      borderRadius: BorderRadius.circular(15)
    ),
              child:Card(
                child:Wrap(children: <Widget>[
                  Container(
                    height:100,
                    child:Image.asset(image),),
                  ListTile(
                    title:Text(text,style:TextStyle(fontWeight: FontWeight.bold)),
                    subtitle: Column(
                      children: [

                         Row(
                           children: [
                             Icon(Icons.star,color: Colors.yellow,size: 15,),
                              SizedBox(width:5),
                               Text(' 5.0 (23 Reviews)'),
                               Text('\$100'),
                           ],
                         ),
                      ],
                    )
                  )
                ],)
              )
            );
}



